mapper.select:
SELECT 

FROM classinfo a

WHERE a.id = #{id}

SELECT 

FROM classinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.profession_id = #{professionId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM classinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE classinfo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
