mapper.select:
SELECT 

FROM recordtype a

WHERE a.id = #{id}

SELECT 

FROM recordtype a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.typekey = #{typekey}


		AND a.score <= #{score}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM recordtype a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE recordtype SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
