mapper.select:
SELECT 

FROM student a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.class_id = #{classId}

	ORDER BY a.score DESC limit 50

SELECT 

FROM student a


	a.del_flag = #{DEL_FLAG_NORMAL} AND a.no = #{no}

SELECT 

FROM student a

WHERE a.id = #{id}

SELECT 

FROM student a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.class_id = #{classId}


		AND a.class_id in

				#{item}



		AND a.no = #{no}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.sushe = #{sushe}


		AND a.zizhucard = #{zizhucard}


		AND a.idcard = #{idcard}


		AND a.phone = #{phone}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM student a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE student SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
