SELECT 

FROM studentscore a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.student_id LIKE 
			AND a.student_id = #{studentId}


		AND a.score <= #{score}




		ORDER BY ${page.orderBy}


